CREATE TRIGGER address_LOG_UPDTRG AFTER UPDATE OF address_id ON address_LOG
BEGIN
UPDATE mm_message_out SET status = \'READY\' WHERE table_name = \'address\' AND status = \'WAITING\' and metrix_log_id = NEW.metrix_log_id AND ((transaction_type = \'INSERT\') OR ((transaction_type = \'UPDATE\' OR transaction_type = \'DELETE\') AND (NEW.address_id IS NULL OR NEW.address_id NOT LIKE \'-%%\')));
END;

CREATE TRIGGER address_UPDTRG AFTER UPDATE OF address_id ON address
BEGIN
UPDATE address_LOG SET address_id = NEW.address_id WHERE address_id = OLD.address_id;
UPDATE place_address SET address_id = NEW.address_id WHERE address_id = OLD.address_id;
UPDATE place_address_LOG SET address_id = NEW.address_id WHERE address_id = OLD.address_id;
END;

