CREATE TRIGGER contract_UPDTRG AFTER UPDATE OF contract_id, contract_version ON contract
BEGIN
UPDATE contract_LOG SET contract_id = NEW.contract_id WHERE contract_id = OLD.contract_id;
UPDATE contract_LOG SET contract_version = NEW.contract_version WHERE contract_version = OLD.contract_version;
UPDATE time_commit SET contract_id = NEW.contract_id WHERE contract_id = OLD.contract_id;
UPDATE time_commit_LOG SET contract_id = NEW.contract_id WHERE contract_id = OLD.contract_id;
UPDATE time_commit SET contract_id = NEW.contract_id WHERE contract_id = OLD.contract_id;
UPDATE time_commit_LOG SET contract_id = NEW.contract_id WHERE contract_id = OLD.contract_id;
UPDATE escalation SET foreign_key_char1 = NEW.contract_id WHERE foreign_key_char1 = OLD.contract_id;
UPDATE escalation_LOG SET foreign_key_char1 = NEW.contract_id WHERE foreign_key_char1 = OLD.contract_id;
UPDATE escalation SET foreign_key_char1 = NEW.contract_id WHERE foreign_key_char1 = OLD.contract_id;
UPDATE escalation_LOG SET foreign_key_char1 = NEW.contract_id WHERE foreign_key_char1 = OLD.contract_id;
UPDATE payment SET contract_id = NEW.contract_id WHERE contract_id = OLD.contract_id;
UPDATE payment_LOG SET contract_id = NEW.contract_id WHERE contract_id = OLD.contract_id;
END;

CREATE TRIGGER contract_DELTRG AFTER DELETE ON contract
BEGIN
DELETE FROM escalation WHERE foreign_key_char1 = OLD.contract_id;
DELETE FROM escalation WHERE foreign_key_char1 = OLD.contract_id;
DELETE FROM escalation WHERE foreign_key_char1 = OLD.contract_id;
END;

