CREATE TRIGGER time_clock_event_UPDTRG AFTER UPDATE OF clock_id, event_sequence ON time_clock_event
BEGIN
UPDATE time_clock_event_LOG SET clock_id = NEW.clock_id WHERE clock_id = OLD.clock_id;
UPDATE time_clock_event_LOG SET event_sequence = NEW.event_sequence WHERE event_sequence = OLD.event_sequence;
END;

CREATE TRIGGER time_clock_rule_LOG_UPDTRG AFTER UPDATE OF clock_id, rule_id ON time_clock_rule_LOG
BEGIN
UPDATE mm_message_out SET status = \'READY\' WHERE table_name = \'time_clock_rule\' AND status = \'WAITING\' and metrix_log_id = NEW.metrix_log_id AND ((transaction_type = \'INSERT\' AND (NEW.clock_id IS NULL OR NEW.clock_id NOT LIKE \'-%%\')) OR ((transaction_type = \'UPDATE\' OR transaction_type = \'DELETE\') AND (NEW.clock_id IS NULL OR NEW.clock_id NOT LIKE \'-%%\') AND (NEW.rule_id IS NULL OR NEW.rule_id NOT LIKE \'-%%\')));
END;

CREATE TRIGGER time_clock_rule_UPDTRG AFTER UPDATE OF clock_id, rule_id ON time_clock_rule
BEGIN
UPDATE time_clock_rule_LOG SET clock_id = NEW.clock_id WHERE clock_id = OLD.clock_id;
UPDATE time_clock_rule_LOG SET rule_id = NEW.rule_id WHERE rule_id = OLD.rule_id;
END;

CREATE TRIGGER time_commit_LOG_UPDTRG AFTER UPDATE OF tc_id, request_id, request_line, request_unit_id, contract_id, contract_version, contract_sequence, task_id, project_id ON time_commit_LOG
BEGIN
UPDATE mm_message_out SET status = \'READY\' WHERE table_name = \'time_commit\' AND status = \'WAITING\' and metrix_log_id = NEW.metrix_log_id AND ((transaction_type = \'INSERT\' AND (NEW.request_id IS NULL OR NEW.request_id NOT LIKE \'-%%\') AND (NEW.request_line IS NULL OR NEW.request_line NOT LIKE \'-%%\') AND (NEW.request_unit_id IS NULL OR NEW.request_unit_id NOT LIKE \'-%%\') AND (NEW.contract_id IS NULL OR NEW.contract_id NOT LIKE \'-%%\') AND (NEW.contract_version IS NULL OR NEW.contract_version NOT LIKE \'-%%\') AND (NEW.contract_sequence IS NULL OR NEW.contract_sequence NOT LIKE \'-%%\') AND (NEW.task_id IS NULL OR NEW.task_id NOT LIKE \'-%%\') AND (NEW.project_id IS NULL OR NEW.project_id NOT LIKE \'-%%\')) OR ((transaction_type = \'UPDATE\' OR transaction_type = \'DELETE\') AND (NEW.tc_id IS NULL OR NEW.tc_id NOT LIKE \'-%%\') AND (NEW.request_id IS NULL OR NEW.request_id NOT LIKE \'-%%\') AND (NEW.request_line IS NULL OR NEW.request_line NOT LIKE \'-%%\') AND (NEW.request_unit_id IS NULL OR NEW.request_unit_id NOT LIKE \'-%%\') AND (NEW.contract_id IS NULL OR NEW.contract_id NOT LIKE \'-%%\') AND (NEW.contract_version IS NULL OR NEW.contract_version NOT LIKE \'-%%\') AND (NEW.contract_sequence IS NULL OR NEW.contract_sequence NOT LIKE \'-%%\') AND (NEW.task_id IS NULL OR NEW.task_id NOT LIKE \'-%%\') AND (NEW.project_id IS NULL OR NEW.project_id NOT LIKE \'-%%\')));
END;

CREATE TRIGGER time_commit_UPDTRG AFTER UPDATE OF tc_id ON time_commit
BEGIN
UPDATE time_commit_LOG SET tc_id = NEW.tc_id WHERE tc_id = OLD.tc_id;
END;

CREATE TRIGGER user_role_LOG_UPDTRG AFTER UPDATE OF user_role ON user_role_LOG
BEGIN
UPDATE mm_message_out SET status = \'READY\' WHERE table_name = \'user_role\' AND status = \'WAITING\' and metrix_log_id = NEW.metrix_log_id AND ((transaction_type = \'INSERT\') OR ((transaction_type = \'UPDATE\' OR transaction_type = \'DELETE\') AND (NEW.user_role IS NULL OR NEW.user_role NOT LIKE \'-%%\')));
END;

CREATE TRIGGER user_role_UPDTRG AFTER UPDATE OF user_role ON user_role
BEGIN
UPDATE user_role_LOG SET user_role = NEW.user_role WHERE user_role = OLD.user_role;
UPDATE role_function_map SET user_role = NEW.user_role WHERE user_role = OLD.user_role;
UPDATE role_function_map_LOG SET user_role = NEW.user_role WHERE user_role = OLD.user_role;
UPDATE person_role SET user_role = NEW.user_role WHERE user_role = OLD.user_role;
UPDATE person_role_LOG SET user_role = NEW.user_role WHERE user_role = OLD.user_role;
END;

CREATE TRIGGER user_role_DELTRG AFTER DELETE ON user_role
BEGIN
DELETE FROM role_function_map WHERE user_role = OLD.user_role;
END;

CREATE TRIGGER warranty_coverage_LOG_UPDTRG AFTER UPDATE OF product_id, wty_cov_id ON warranty_coverage_LOG
BEGIN
UPDATE mm_message_out SET status = \'READY\' WHERE table_name = \'warranty_coverage\' AND status = \'WAITING\' and metrix_log_id = NEW.metrix_log_id AND ((transaction_type = \'INSERT\' AND (NEW.product_id IS NULL OR NEW.product_id NOT LIKE \'-%%\')) OR ((transaction_type = \'UPDATE\' OR transaction_type = \'DELETE\') AND (NEW.product_id IS NULL OR NEW.product_id NOT LIKE \'-%%\') AND (NEW.wty_cov_id IS NULL OR NEW.wty_cov_id NOT LIKE \'-%%\')));
END;

CREATE TRIGGER warranty_coverage_UPDTRG AFTER UPDATE OF product_id, wty_cov_id ON warranty_coverage
BEGIN
UPDATE warranty_coverage_LOG SET product_id = NEW.product_id WHERE product_id = OLD.product_id;
UPDATE warranty_coverage_LOG SET wty_cov_id = NEW.wty_cov_id WHERE wty_cov_id = OLD.wty_cov_id;
END;

