END;

CREATE TRIGGER wty_cov_type_UPDTRG AFTER UPDATE OF wty_cov_type ON wty_cov_type
BEGIN
UPDATE wty_cov_type_LOG SET wty_cov_type = NEW.wty_cov_type WHERE wty_cov_type = OLD.wty_cov_type;
END;
